mapper.select:
select  *
from(select user_main.userId,
user_main.mobile,
user_main.nickName
FROM user_main


    mobile = #{mobile}


)as tmp_user
INNER JOIN (
SELECT *
FROM general_journal


      journalType = #{journalType}


      and endTime < #{endTime}


      and startTime > #{startTime}


)as tmp_gj
ON tmp_gj.userId=tmp_user.userId ORDER BY endTime desc;

select  *
from(select user_main.userId,
user_main.mobile,
user_main.nickName
FROM user_main


    mobile = #{mobile}


)as tmp_user
INNER JOIN (
SELECT *
FROM general_journal


    journalType = #{journalType}


    and endTime < #{endTime}


    and startTime > #{startTime}


)as tmp_gj
ON tmp_gj.userId=tmp_user.userId ORDER BY endTime desc  LIMIT #{pagestart},#{pagecount};

select  count(*)
from
(
