mapper.select:
SELECT plan_date, fp_order_id, user_id, credit_id, sum(plan_principal) 'cred_plan_principal', sum(plan_interest) 'cred_plan_interest' FROM fp_main.credit_repayment
user_id = #{userId}
AND fp_order_id = #{orderId}
AND plan_date >= #{startTime}
AND plan_date <= #{endTime}
GROUP BY credit_id ORDER BY credit_id DESC

SELECT a.plan_date, a.credit_id, a.user_id, a.fp_order_id, a.red_plan_amount, a.term_num 'red_term_num', b.local_Info 'red_local_Info', b.red_package_type 'red_package_type' FROM ( SELECT credit_id, user_id, plan_date, fp_order_id, sum(plan_amount) red_plan_amount, term_num FROM fp_main.red_package_repayment
AND user_id = #{userId}
AND fp_order_id = #{orderId}
AND plan_date >= #{startTime}
AND a.plan_date <= #{endTime}
) AS a INNER JOIN fp_main.red_package_rec AS b ON a.fp_order_id = b.fp_order_id GROUP BY a.credit_id

SELECT a.fp_order_id, a.user_id, b.plan_date, b.credit_id, a.rate 'vip_rate', sum(b.plan_amount) 'vip_plan_amount', b.term_num 'vip_term_num' FROM fp_main.vip_interest_rec AS a , fp_main.vip_interest_repayment AS b
a.fp_order_id = b.fp_order_id
AND a.user_id = #{userId}
AND a.fp_order_id = #{orderId}
AND b.plan_date >= #{startTime}
AND b.plan_date <= #{endTime}
GROUP BY b.credit_id ORDER BY b.credit_id DESC

SELECT a.invest_id fp_order_id, b.invest_user user_id, b.credit_id, b.plan_date, b.type 'pf_type', b.term_num 'pf_term_num', sum(b.plan_amount) 'pf_plan_amount' FROM pf_interest.record AS a , pf_interest.repayment AS b
a.record_id = b.record_id
AND a.invest_user = #{userId}
AND a.invest_id = #{orderId}
AND b.plan_date >= #{startTime}
AND b.plan_date <= #{endTime}
GROUP BY b.credit_id ORDER BY b.credit_id DESC
