mapper.select:
select
*
from customer_service_phone_record
where 1=1

  AND phone_number = #{phoneNumber}


  AND worker_number = #{workerNumber}


  AND status = #{status}


  and #{gmtCreate}<gmtCreate

SELECT cp.*,cj.begin_time,cj.end_time FROM customer_service_phone_record AS cp LEFT JOIN customer_service_journal AS cj ON cp.id= cj.service_id WHERE 1=1
AND cp.phone_number = #{phoneNumber}
AND cp.worker_number = #{workerNumber}
AND cp.status = #{status}
and #{gmtCreate}<beginTime
and #{gmtCreate}>endTime
order by cp.gmt_create desc

SELECT
count(*)
FROM customer_service_phone_record AS cp
WHERE 1=1

  AND cp.phone_number = #{phoneNumber}


  AND cp.worker_number = #{workerNumber}


  AND cp.status = #{status}


  and cp.gmt_create>#{beginTime}


  and cp.gmt_create<#{endTime}

order by cp.gmt_create desc

SELECT
*
FROM  customer_service_phone_record


  AND phone_number = #{phoneNumber}


  AND worker_number = #{workerNumber}


  AND status = #{status}


  and gmt_create>#{beginTime}


  and gmt_create<#{endTime}


order by gmt_create desc
