mapper.insert:
insert into user (work_number, username,
password, email, phone,
gmt_create, gmt_modify, status
)
values (#{workNumber,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR},
#{password,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR},
#{gmtCreate,jdbcType=BIGINT}, #{gmtModify,jdbcType=BIGINT}, #{status,jdbcType=SMALLINT}
)

mapper.select:
select user_id from user
where 1=1

    and work_number = #{workNumber,jdbcType=INTEGER}


    and username = #{username,jdbcType=VARCHAR}


    and email = #{email,jdbcType=VARCHAR}


    and phone = #{phone,jdbcType=VARCHAR}

and status = 0

mapper.update:
update user
SET
password = #{password,jdbcType=VARCHAR},
gmt_modify = #{gmtModify,jdbcType=BIGINT}
where user_id = #{userId,jdbcType=BIGINT}

UPDATE user SET status = 1
where 1=1 AND

     user_id = #{userId,jdbcType=BIGINT}

UPDATE user SET status = 0
where 1=1 AND

    user_id = #{userId,jdbcType=BIGINT}
